import React from 'react';
import { Typewriter , cursor, useTypewriter } from 'react-simple-typewriter'
import './Intro.css';

const Intro = () => {
  const {text} = useTypewriter({
    words: [ " Developer."," Learner."," Coder."," Self Believer."],
    loop: 0, 
    typeSpeed: 70,
    deleteSpeed: 50,
    delaySpeed: 1000
    })
  return (
    <>
    <section className='intro-section'>
    <div className='container intro-inner d-flex justify-content-center align-items-center'>
        <div className='intro-box'>
          <h6 className='intro-welcome'>Hi, I'm</h6>
          <h1 className='intro-name'>Naveen Kolhapur</h1>
          <h2 className="intro-role"><Typewriter
                loop={0}
                    cursor
                    cursorStyle="_"
                    typeSpeed={70}
                    deleteSpeed={50}
                    delaySpeed={2000}
                    words={['<Frontend Developer/>', '<Web Developer/>' , '<UI Developer/>']}
                /></h2>
          <div className='intro-buttons mt-4'>
            <button className='intro-btn-1'>Get in Touch</button>
          </div>
        </div>
    </div>
    </section>
    </>
  )
}

export default Intro
